import React, { useState } from 'react';
import { gql, useLazyQuery } from "@apollo/client";
import Footer from './components/footer/Footer';
import Search from './components/search/Search';
import Body from './components/body/Body';
import './styles/main.css'

const GET_WEATHERDATA = gql`
  query Query($inpValue: String) {
    weather(inpValue: $inpValue) {
      location {
        city
        country
        localtime
      }
      values {
        temp
        pressure
        humidity
        precip
        windDir
        windSpeed
        windGust
        cloud
        vis
      }
    }
  }
`


function App() {
  const [inpValue, setInpValue] = useState(null);

  const [getWeather, response] = useLazyQuery(GET_WEATHERDATA, {
    fetchPolicy: 'network-only',
    variables: { inpValue },
    pollInterval: 60000,
  });

  return (
    <div className='app'>
      <Search getWeather={getWeather} />
      {response && <Body response={response} />}
      <Footer />
    </div>
  );
}

export default App;
