import React, { useState } from 'react';
import { gql, useLazyQuery } from "@apollo/client";
import Footer from './components/footer/Footer';
import Search from './components/search/Search';
import Body from './components/body/Body';
import './styles/main.css'

const GET_WEATHERDATA = gql`
  query Query($inpValue: String) {
    weather(inpValue: $inpValue) {
      location {
        city
        country
        localtime
      }
      values {
        temp
        pressure
        humidity
        precip
        windDir
        windSpeed
        windGust
        cloud
        vis
      }
    }
  }
`


function App() {
  const [getWeather, { loading, error, data }] = useLazyQuery(GET_WEATHERDATA);
  const [isQueryExecuted, setIsQueryExecuted] = useState(false);

  const handleGetWeather = (props) => {
    getWeather(props);
    setIsQueryExecuted(true);
  };

  return (
    <div className='app'>
      <Search handleGetWeather={handleGetWeather} />
      {isQueryExecuted && <Body loading={loading} error={error} data={data}/>}
      <Footer />
    </div>
  );
}

export default App;
